import React from 'react';
import './drawer.css';

const Drawer = ({ isOpen, onClose, children }) => {
    return (
        <div className={`drawer ${isOpen ? 'open' : ''}`}>
            <div className="drawer-content">
                {children}
                <button className="close-button" onClick={onClose}>Close</button>
            </div>
        </div>
    );
};

export default Drawer;
