import React, {useState } from 'react';
import './home.css';
import Header from './header.js';
import ProductCard from './productcard.js';


function Home() {

    const products = [
        {
            id: 1,
            title: "Call Center Software",
            description: "Manage customer interactions across various channels."
        },
        {
            id: 2,
            title: "Business Phone System",
            description: "Enhance communication within your organization."
        },
        {
            id: 3,
            title: "Omnichannel Solutions",
            description: "Integrate multiple communication channels seamlessly."
        },
        {
            id: 4,
            title: "Workflows Automation",
            description: "Automate repetitive tasks and streamline operations."
        },
        // Add more products as needed
    ];

    const [currentIndex, setCurrentIndex] = useState(0);

    const nextSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex === products.length - 1 ? 0 : prevIndex + 1));
    };

    const prevSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex === 0 ? products.length - 1 : prevIndex - 1));
    };
    
    return (
        <div className="homepage">
            <div className='header-div'>
                    <Header />
            </div>
            <div id="home" className="home">
                <h2>homepage Section</h2>
                <p>Explore our home.</p>
                <div className="carousel">
                    <div className="product-cards">
                        {products.slice(currentIndex, currentIndex + 3).map(product => (
                            <ProductCard key={product.id} title={product.title} description={product.description} />
                        ))}
                    </div>
                    <button className="prev" onClick={prevSlide}>&#10094;</button>
                    <button className="next" onClick={nextSlide}>&#10095;</button>
                </div>
            </div>
            <div id="about" className="section">
                <h2>Who We Are</h2>
                <p>Welcome to White Line, a premier offshore call center based in Bangalore, India. We specialize in providing high-quality customer support services tailored to meet the unique needs of clients in the Gulf Cooperation Council (GCC) region. 
                    At White Line, our team of dedicated professionals is committed to delivering exceptional customer service, ensuring that every interaction reflects the values and standards of our clients. With a deep understanding of the GCC market and a strong focus on customer satisfaction, we strive to create positive experiences and build lasting relationships with customers.
                    Our state-of-the-art facility in Bangalore is equipped with the latest technology and infrastructure, enabling us to offer efficient and reliable support solutions. We pride ourselves on our ability to seamlessly integrate with our clients' operations, providing customized services that enhance customer engagement and drive business success.
                    Choose White Line as your trusted partner for superior customer support in the GCC region. Let us help you elevate your customer service to newÂ heights.

</p>
            </div>
            <div id="services" className="section">
                <h2>Services Section</h2>
                <p>Explore our services.</p>
            </div>
            <div id="pricing" className="section">
                <h2>Pricing Section</h2>
                <p>View our pricing plans.</p>
            </div>
            <div id="blog" className="section">
                <h2>Blog Section</h2>
                <p>Read our latest blog posts.</p>
            </div>
        </div >
    );
}

export default Home;
