import React, { useEffect, useState } from 'react';
import './home.css';
import Header from './header.js';
import image1 from './assets/partner1.jpg'; // Replace with your actual image path
import image2 from './assets/partner2.jpg'; // Replace with your actual image path

const Home = () => {
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        // Delay the appearance of the content
        const timer = setTimeout(() => {
            setIsVisible(true);
        }, 1000); // Adjust the delay as needed (e.g., 1000ms = 1s)

        return () => clearTimeout(timer);
    }, []);

    return (
        <div className="homepage">
            <div className='header-div'>
                <Header />
            </div>
            <div className={`content ${isVisible ? 'visible' : ''}`}>
                <h1>Our Partners</h1>
                <div className="tiles-container">
                    <div className="tile">
                        <img src={image1} alt="Partner 1" />
                    </div>
                    <div className="tile">
                        <img src={image2} alt="Partner 2" />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Home;
