import React, { useEffect, useState } from 'react';
import './home.css';
import Header from './header.js';
import image1 from './assets/partner1.jpg'; // replace with your actual image path
import image2 from './assets/partner2.jpg'; // replace with your actual image path

const Home = () => {
    const [isContentVisible, setIsContentVisible] = useState(false);

    useEffect(() => {
        const timer = setTimeout(() => {
            setIsContentVisible(true);
        }, 6000); // Show content after 6 seconds

        return () => clearTimeout(timer);
    }, []);

    return (
        <div className="homepage">
            <div className="header-div">
                <Header />
            </div>
            <div className="welcome-text">
                Welcome to White Line Offshore Call Center
            </div>
            <div className={`content ${isContentVisible ? 'show' : ''}`}>
                <h1>Our Partners</h1>
                <div className={`tiles-container ${isContentVisible ? 'show' : ''}`}>
                    <a href="https://www.tatatelebusiness.com" target="_blank" rel="noopener noreferrer">
                        <div className="tile">
                            <img src={image1} alt="Partner 1" />
                        </div>
                    </a>
                    <a href="https://www.vodafone.com" target="_blank" rel="noopener noreferrer">
                        <div className="tile">
                            <img src={image2} alt="Partner 2" />
                        </div>
                    </a>
                </div>
                <div className={`number ${isContentVisible ? 'show' : ''}`}>
                    <h2>Call Us</h2>
                    <span>9633220088</span>
                </div>
            </div>
        </div>
    );
};

export default Home;
