import React from 'react';
import './drawer_content.css'; // Reusing the same CSS file

const WhatWeDo = () => {
    return (
        <div className="what-we-do">
            <h2>Our Services</h2>
            <p>White Line Offshore Call Center offers a comprehensive suite of outsourcing services designed to optimize business operations and enhance customer experience:</p>
            <ul>
                <li><strong>Customer Support</strong>: Multi-channel support (phone, email, chat) for seamless customer interactions.</li>
                <li><strong>Technical Support</strong>: Expert troubleshooting and resolution of technical issues to minimize downtime.</li>
                <li><strong>Telemarketing</strong>: Strategic campaigns to boost sales and market penetration.</li>
                <li><strong>Back Office Services</strong>: Efficient management of administrative tasks to improve productivity.</li>
                <li><strong>Language Capabilities</strong>: Fluent in English, Arabic, Hindi, and Malayalam to cater to diverse clientele.</li>
            </ul>
            <h3>Why Choose White Line Offshore Call Center?</h3>
            <ul>
                <li><strong>Expertise</strong>: Industry-leading expertise with a proven track record of delivering exceptional outsourcing solutions.</li>
                <li><strong>Quality Assurance</strong>: Rigorous quality standards to ensure consistent service excellence.</li>
                <li><strong>Scalability</strong>: Flexible solutions to accommodate varying business needs and growth.</li>
                <li><strong>Cost Efficiency</strong>: Competitive pricing models to maximize cost savings without compromising quality.</li>
            </ul>
        </div>
    );
};

export default WhatWeDo;
