import React, { useState } from 'react';
import './header.css';
import logo from './assets/logo.png';
import Drawer from './drawer';
import WhoWeAre from './WhoWeAre';
import WhyChooseUs from './WhyChooseUs';
import WhatWeDo from './WhatWeDo';
import ContactUs from './ContactUs';

const Header = () => {
    const [isDrawerOpen, setDrawerOpen] = useState(false);
    const [drawerContent, setDrawerContent] = useState(null);

    const toggleDrawer = (content) => {
        setDrawerContent(content);
        setDrawerOpen(true);
    };

    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    return (
        <header className="headers">
            <div className="logo">
                <img src={logo} alt="Logo" />
            </div>
            <nav className="nav">
                <a href="#home" onClick={(e) => { e.preventDefault(); toggleDrawer(<WhoWeAre />); }}>Who are we</a>
                <a href="#about" onClick={(e) => { e.preventDefault(); toggleDrawer(<WhyChooseUs />); }}>Why Choose Us</a>
                <a href="#services" onClick={(e) => { e.preventDefault(); toggleDrawer(<WhatWeDo />); }}>What Do We Do</a>
                <a href="#pricing" onClick={(e) => { e.preventDefault(); toggleDrawer(<ContactUs />); }}>Contact Us</a>
            </nav>
            <div className="menu-icon" onClick={toggleMenu}>
                â˜°
            </div>
            <div className={`menu-drawer ${isMenuOpen ? 'open' : ''}`}>
                <div className="drawer-content">
                    <a href="#home" onClick={toggleMenu}>Who are we</a>
                    <a href="#about" onClick={toggleMenu}>Why Choose Us</a>
                    <a href="#services" onClick={toggleMenu}>What Do We Do</a>
                    <a href="#pricing" onClick={toggleMenu}>Contact Us</a>
                </div>
            </div>
            <Drawer isOpen={isDrawerOpen} onClose={() => setDrawerOpen(false)}>
                {drawerContent}
            </Drawer>
        </header>
    );
};

export default Header;
